mapper.select:
SELECT board_idx, id, title, content, img_url, created_date, view
FROM board 
WHERE board_idx = #{board_idx}

mapper.update:
UPDATE board
SET title = #{title}, 
    content = #{content},
    img_url = #{img_url}
WHERE board_idx = #{board_idx}

UPDATE board
SET view = view + 1
WHERE board_idx = #{board_idx}
